var arr = macro("$IcingaCheckNLA_Array_NICs$");
    if (len(arr) == 0) {
        return "@()";
    }
    return arr.map(
        x => if (typeof(x) == String) {
            var argLen = len(x);
            if (argLen != 0 && x.substr(0,1) == "'" && x.substr(argLen - 1, argLen) == "'") {
                x;
            } else {
                "'" + x + "'";
            }
        } else {
            x;
        }
    ).join(",");
